// Here all the Base values are calculated
function incBase(idTag){
	var variable =document.getElementById(idTag).value;
	document.getElementById(idTag).value = +variable + +1;
}

function decBase(idTag){
	var variable =document.getElementById(idTag).value;
	document.getElementById(idTag).value = +variable - +1;
	calcMod()
}

// Here all the Modifiers values are calculated

function calcMod(){
	var modStr;
	var str = document.getElementById('dispStr').value;
	modStr = Math.floor((str/2)-5)
	if (modStr>0){
		document.getElementById('modifierStr').value = '+' + modStr;
	}else{
		document.getElementById('modifierStr').value = modStr;	
	}
	
}